/**
 * Performs the given action for each element of the {@code Iterable}
 * until all elements have been processed or the action throws an
 * exception.  Actions are performed in the order of iteration, if that
 * order is specified.  Exceptions thrown by the action are relayed to the
 * caller.
 * <p>
 * The behavior of this method is unspecified if the action performs
 * side-effects that modify the underlying source of elements, unless an
 * overriding class has specified a concurrent modification policy.
 *
 * @implSpec
 * <p>The default implementation behaves as if:
 * <pre>{@code
 *     for (T t : this)
 *         action.accept(t);
 * }</pre>
 *
 * @param action The action to be performed for each element
 * @throws NullPointerException if the specified action is null
 * @since 1.8
 */
default void forEach(Consumer<? super T> action) {
    Objects.requireNonNull(action);
    for (T t : this) {
        action.accept(t);
    }
}

@Override
public void deductStock(List<OrderDetailDTO> items) {
    String sqlStatement = "com.hmall.mapper.ItemMapper.updateStock";
    boolean r = false;
    try {
        r = executeBatch(items, (sqlSession, entity) -> sqlSession.update(sqlStatement, entity));
    } catch (Exception e) {
        throw new BizIllegalException("更新库存异常，可能是库存不足!", e);
    }
    if (!r) {
        throw new BizIllegalException("库存不足！");
    }
}

@Autowired
private ItemMapper itemMapper;

public void deductStock(List<OrderDetailDTO> items) {
    for (OrderDetailDTO item : items) {
        int updated = itemMapper.updateStock(item.getId(), item.getNum());
        if (updated <= 0) {
            throw new BizIllegalException("库存不足或更新失败");
        }
    }
}

private void checkCartsFull(Long userId) {
    int count = lambdaQuery().eq(Cart::getUserId, userId).count();
    if (count >= cartProperties.getMaxItems ()) {
        throw new BizIllegalException(StrUtil.format("用户购物车课程不能超过{}", cartProperties.getMaxItems()));
    }
}

@Data
@Component
@ConfigurationProperties(prefix = "hmall.cart")
public class CartProperties {
    private Integer maxItems = 100; // 默认最大商品数量为 100
}

private void checkCartsFull(Long userId) {
    int count = lambdaQuery().eq(Cart::getUserId, userId).count();
    int max = cartProperties.getMaxItems() != null ? cartProperties.getMaxItems() : 100; // 默认值 100
    if (count >= max) {
        throw new BizIllegalException(StrUtil.format("用户购物车课程不能超过{}", max));
    }
}

